import logo from './logo.svg';
import './App.css';
import React from 'react';

class StarWars extends React.Component {

  constructor() {
    super()
    this.state = {
      name: 'luke',
      height: 172,
      homeworld: 'tattone',
      films: ['it1', 'it2']
    }
  }

  getCharacter() {
    const url = "https://rawcdn.githack.com/akabab/starwars-api/0.2.1/api/id/1.json";
    fetch(url)
      .then(response => response.json)
      .then(data => {
        console.log(data)
  })
  }

  render() {
    return(
      <div>
        <h1>{this.state.name}</h1>
        <p>Height:{this.state.height}</p>
        <p>HomeWorld:{this.state.homeworld}</p>
        <ul>
          <li>
            {this.state.films}
          </li>
        </ul>
        <button type="button" className="btn" onClick={() => getCharacter()}>Randomize generator</button>
      </div>
    )
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <StarWars />
      </header>
    </div>
  );
}

export default App;
