import './App.css';
import React from 'react';

class StarWars extends React.Component {

  constructor() {
    super()
    this.state = {
      name: null,
      height: null,
      homeworld: null,
      films: [],
    }
  }

  getNewCharacter() {
    const url = "https://rawcdn.githack.com/akabab/starwars-api/0.2.1/api/id/1.json"
    fetch(url)
      .then(response => response.json)
      .then(data => {
        console.log(data)
