        })

      })
  }

  render() {
    return(
      <div>
        <h1>{this.state.name}</h1>
        <p>Height:{this.state.height}</p>
        <p>HomeWorld:{this.state.homeworld}</p>
        <ul>
          <li>
            {this.state.films}
          </li>
        </ul>
        <button 
          type="button" 
          className="btn" 
          onClick={() => this.getNewCharacter()}
        >
          Randomize generator
        </button>
      </div>
    )
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <StarWars />
      </header>
    </div>
  );
}

export default App;
